User.destroy_all
Item.destroy_all
Collection.destroy_all
Exhibit.destroy_all
Museum.destroy_all
Curator.destroy_all

15.times do
    User.create(username: Faker::Internet.username , password: Faker::Internet.password , name_first: Faker::Name.first_name , name_last: Faker::Name.last_name , location: Faker::Address.country, picture: @people.sample )
end

10.times do
    Collection.create(name: Faker::Commerce.department ,user_id: User.all.sample.id )
end

5.times do
    Museum.create(name: Faker::Company.name, field_of_study: Faker::Commerce.department, information: Faker::String.random )       
end

10.times do 
    Exhibit.create(name: Faker::Company.industry , picture: @contemporary_art.sample , category: "contemporary art" , date: Faker::Time.forward , museum_id: Museum.all.sample.id)
end

30.times do
    Item.create(name: Faker::Commerce.product_name, year_created: -1960 , description: Faker::String.random , picture: @contemporary_art.sample , classification: "contemporary art", collection_id: Collection.all.sample.id , exhibit_id: Exhibit.all.sample.id)
end

5.times do
    Curator.create(user_id: User.all.sample.id, museum_id: Museum.all.sample.id)
end
